import React, { useContext, useState } from 'react';
import { useHistory, Redirect } from 'react-router-dom';

import FoodAppContext from '../context/FoodAppContext';
import drinkIcon from '../images/drinkIcon.svg';
import exploreIcon from '../images/exploreIcon.svg';
import mealIcon from '../images/mealIcon.svg';
import '../styles/footer.css';

function Footer() {
  const [toggle, settoggle] = useState(false);
  const history = useHistory();

  const { setShowSearch } = useContext(FoodAppContext);

  let rotas = '';

  const handlerClick = ({ target }) => {
    settoggle(true);
    setShowSearch(false);
    rotas = target.name;
    return rotas;
  };

  if (toggle) {
    return <Redirect to={ rotas } />;
  }

  return (
    <div className="footer" data-testid="footer">
      <button
        type="button"
        data-testid="drinks-bottom-btn"
        onClick={ handlerClick }
        src={ drinkIcon }
      >
        <img className="rocksGlass" name="/bebidas" src={ drinkIcon } alt="drink" />
      </button>
      <button
        type="button"
        data-testid="explore-bottom-btn"
        onClick={ handlerClick }
        src={ exploreIcon }
      >
        <img className="rocksGlass" name="/explorar" src={ exploreIcon } alt="explore" />
      </button>
      <button
        type="submit"
        data-testid="food-bottom-btn"
        onClick={ handlerClick }
        src={ mealIcon }
      >
        <img className="rocksGlass" name="/comidas" src={ mealIcon } alt="meal" />
      </button>
    </div>
  );
}

export default Footer;
