import React, { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import FoodAppContext from '../context/FoodAppContext';

function MainMeals() {
  const { mealsData, drinksData } = useContext(FoodAppContext);
  const history = useHistory();
  const { location } = history;
  const { pathname } = location;
  const recipe = pathname.split('/');
  const recipesData = recipe === 'comidas' ? mealsData : drinksData;
  const keys = recipe === 'comidas' ? 'idMeal, strMeal, strMealThumb'
    : 'idDrink, strDrink, strDrinkThumb';

  const handleToDetail = (id) => {
    handleClickDetail(id, 'comidas');
    history.push(`/comidas/${id}`);
  };

  const zero = 0;
  const doze = 12;

  return (
    <section>
      <section className="section-meals">
        {recipesData && recipesData
          .slice(zero, doze)
          .map(({ idMeal, strMeal, strMealThumb }, index) => (
            <button
              type="button"
              key={ idMeal }
              className="div-meals"
              data-testid={ `${index}-recipe-card` }
              onClick={ () => handleToDetail(idMeal) }
            >
              <img
                src={ strMealThumb }
                alt="meals"
                data-testid={ `${index}-card-img` }
              />
              <p
                data-testid={ `${index}-card-name` }
              >
                {strMeal}
              </p>
            </button>
          ))}
      </section>
    </section>
  );
}

export default MainMeals;
