import React, { useContext } from 'react';
import { Redirect } from 'react-router-dom';
import FoodAppContext from '../context/FoodAppContext';

function FormLogin() {
  const { getFilterEmail, email, password, route, handlerChangeLogin,
    handlerClickLogin } = useContext(FoodAppContext);

  if (route) return <Redirect to="/comidas" />;

  return (
    <div className="form-login">

      <h1>Login</h1>

      <input
        type="text"
        name="email"
        data-testid="email-input"
        onChange={ handlerChangeLogin }
      />

      <input
        type="password"
        name="password"
        data-testid="password-input"
        onChange={ handlerChangeLogin }
      />

      <button
        type="button"
        data-testid="login-submit-btn"
        disabled={ getFilterEmail(email, password) }
        onClick={ handlerClickLogin }
      >
        Entrar
      </button>
    </div>
  );
}

export default FormLogin;
