import React, { useContext } from 'react';

import FoodAppContext from '../context/FoodAppContext';

function MainDrinks() {
  const { mealsData, drinksData } = useContext(FoodAppContext);
  if (!drinksData) return <h2>Carregando...</h2>;
  const zero = 0;
  const doze = 12;
  return (
    <section className="section-refeicao">
      {drinksData.slice(zero, doze).map(({ idDrink, strDrink, strDrinkThumb }) => (
        <div key={ idDrink } className="div-refeicao" data-testid={ strDrink }>
          <img src={ strDrinkThumb } alt="drinks" />
          <p>{ strDrink }</p>
        </div>
      ))}
    </section>
  );
}

export default MainDrinks;
