import React, { useContext } from 'react';
import FoodAppContext from '../context/FoodAppContext';

function Video() {
  const { detailRecipe } = useContext(FoodAppContext);
  const { meals } = detailRecipe;

  return (
    <div className="div-video">
      <h3>Video Embeded</h3>
      {meals && meals.map(({ idMeal, strYoutube }) => (
        <iframe 
          width="341" 
          height="160" src={ strYoutube } frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
      ))}
    </div>
  );
}

export default Video;
