import { useState } from 'react';

function useSearch() {
  const [fields, setFields] = useState({ term: '', type: '' });

  const handlerChange = ({ target }) => {
    const { name, value } = target;
    setFields({
      ...fields,
      [name]: value,
    });
  };

  return [handlerChange];
}

export default useSearch;
