import React from 'react';

function useRecipes(history) {
    const { location } = history;
    const { pathname } = location;
    const recipes = pathname.split('/');
    const zero = 0;
    const doze = 12;

    const handleClickDetail = async (pathname, id) => {
        const detail = await detailApi(id, pathname);

        setDetailRecipe(detail);
      };

    const handleToDetail = (id) => {
      handleClickDetail(id, recipes[1]);
      history.push(`/${recipes[1]}/${id}`);
    };

    return [handleClickDetail, handleToDetail, recipes, zero, doze];

}

export default useRecipes;
