import React, { useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import useCategory from '../hooks/useCategory';

import { mealsAPI, drinksAPI } from '../services';

function Category() {
  const history = useHistory();
  const [cinco, zero, bools, category, setMealsData, setDrinksData, showSearch,
    mealsCategory, drinksCategory, toggle, setToggle,
    fetchCategory] = useCategory(history);

  useEffect(() => {
    fetchCategory();
  }, []);

  if (category[1] === 'comidas') {
    return (
      <div className="div-category">
        <button
          type="button"
          data-testid="All-category-filter"
          hidden={ showSearch ? bools : false }
          onClick={ async () => {
            const { meals } = await mealsAPI('', '');
            setMealsData(meals);
          } }
        >
          All
        </button>
        {mealsCategory && mealsCategory
          .slice(zero, cinco)
          .map(({ strCategory }, index) => (
            <button
              type="button"
              key={ index }
              data-testid={ `${strCategory}-category-filter` }
              hidden={ showSearch ? bools : false }
              value={ strCategory }
              onMouseMove={ () => setToggle(false) }
              onClick={ handlerCategory }
            >
              { strCategory}
            </button>
          ))}
      </div>
    );
  }
  return (
    <div className="div-category">
      <button
        type="button"
        data-testid="All-category-filter"
        hidden={ showSearch ? bools : false }
        onClick={ async () => {
          const { drinks } = await drinksAPI('', '');
          setDrinksData(drinks);
        } }
      >
        All
      </button>
      {drinksCategory && drinksCategory
        .slice(zero, cinco)
        .map(({ strCategory }, index) => (
          <button
            type="button"
            key={ index }
            data-testid={ `${strCategory}-category-filter` }
            hidden={ showSearch ? bools : false }
            value={ strCategory }
            onMouseMove={ () => setToggle(false) }
            onClick={ async ({ target }) => {
              setToggle(false);
              const term = toggle ? '' : target.value;
              const { drinks } = await drinksAPI(term, 'c');
              setDrinksData(drinks);
              setToggle(true);
            } }
          >
            { strCategory}
          </button>
        ))}
    </div>
  );
}

export default Category;
