import React, { useContext, useState } from 'react';
import { Redirect } from 'react-router-dom';

import FoodAppContext from '../context/FoodAppContext';
import drinkIcon from '../images/drinkIcon.svg';
import exploreIcon from '../images/exploreIcon.svg';
import mealIcon from '../images/mealIcon.svg';
import '../styles/footer.css';

function Footer() {
  const [test, settest] = useState(false);
 // const history = useHistory();

  const { setShowSearch } = useContext(FoodAppContext);

  const rotasChange = (rota) => {
    console.log(rota);
    setShowSearch(false);
    return <Redirect to={ rota } />;
  };

/*   function handleClickExplore({ target }) {
    settest(true);
    setShowSearch(false);
    rota = target.value;
  }

  function handleClickMeals({ target }) {
    settest(true);
    rota = target.value;
  }
 */

  return (
    <div className="footer" data-testid="footer">
      <button
        type="button"
        data-testid="drinks-bottom-btn"
        value="/bebidas"
        onClick={ ({ target }) => {
          rotasChange(target);
        } }
        src={ drinkIcon }
      >
        <img className="rocksGlass" value="/bebidas" src={ drinkIcon } alt="drink" />
      </button>
      <button
        type="button"
        data-testid="explore-bottom-btn"
        value="/explorar"
        onClick={ ({ target }) => {
          rotasChange(target.value);
        } }
        src={ exploreIcon }
      >
        <img className="rocksGlass" value="/explorar" src={ exploreIcon } alt="explore" />
      </button>
      <button
        type="submit"
        data-testid="food-bottom-btn"
        value="/comidas"
        onClick={ ({ target }) => {
          rotasChange(target.value);
        } }
        src={ mealIcon }
      >
        <img className="rocksGlass" value="/comidas" src={ mealIcon } alt="meal" />
      </button>
    </div>
  );
}

export default Footer;
