import React, { useContext, useState } from 'react';
import { useHistory, Redirect } from 'react-router-dom';

import FoodAppContext from '../context/FoodAppContext';
import drinkIcon from '../images/drinkIcon.svg';
import exploreIcon from '../images/exploreIcon.svg';
import mealIcon from '../images/mealIcon.svg';
import '../styles/footer.css';

let rota = '';
function Footer() {
  const [test, settest] = useState(false);
  const history = useHistory();

  const { setShowSearch } = useContext(FoodAppContext);

  const handleClick = ({ target }) => {
    console.log(target.value);
    setShowSearch(false);
    return <Redirect to={ target.value } />;
  };

/*   function handleClickExplore({ target }) {
    settest(true);
    setShowSearch(false);
    rota = target.value;
  }

  function handleClickMeals({ target }) {
    settest(true);
    rota = target.value;
  }
 */

  return (
    <div className="footer" data-testid="footer">
      <button
        type="button"
        data-testid="drinks-bottom-btn"
        value="/bebidas"
        onClick={ handleClick }
        src={ drinkIcon }
      >
        <img className="rocksGlass" src={ drinkIcon } alt="drink" />
      </button>
      <button
        type="button"
        data-testid="explore-bottom-btn"
        value="/explorar"
        onClick={ handleClick }
        src={ exploreIcon }
      >
        <img className="rocksGlass" src={ exploreIcon } alt="explore" />
      </button>
      <button
        type="submit"
        data-testid="food-bottom-btn"
        value="/comidas"
        onClick={ handleClick }
        src={ mealIcon }
      >
        <img className="rocksGlass" src={ mealIcon } alt="meal" />
      </button>
    </div>
  );
}

export default Footer;
