import React, { useContext, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';

import FoodAppContext from '../context/FoodAppContext';
import { categoryMealApi, categoryDrinkApi, mealsAPI, drinksAPI } from '../services';

function Category() {
  const {
    setMealsData,
    setDrinksData,
    showSearch,
  } = useContext(FoodAppContext);
  const [mealsCategory, setMealsCategory] = useState([]);
  const [drinksCategory, setDrinksCategory] = useState([]);
  const [toggle, setToggle] = useState(false);

  const history = useHistory();
  const { location } = history;
  const { pathname } = location;
  const cat = pathname.split('/');

  const categorys = cat[1] === 'comidas' ? mealsCategory : drinksCategory;

  async function fecthCategoryMeals() {
    const { meals } = await categoryMealApi();
    setMealsCategory(meals);
  }

  async function fecthCategoryDrinks() {
    const { drinks } = await categoryDrinkApi();
    setDrinksCategory(drinks);
  }

  const fetchCategory = cat[1] === 'comidas' ? fecthCategoryMeals : fecthCategoryDrinks;

  useEffect(() => {
    fetchCategory();
  }, []);

  const cinco = 5;
  const zero = 0;
  const bools = true;

  return (
    <div className="div-category">
      <button
        type="button"
        data-testid="All-category-filter"
        hidden={ showSearch ? bools : false }
        onClick={ async () => {
          const { meals } = await mealsAPI('', '');
          setMealsData(meals);
        } }
      >
        All
      </button>
      {categorys && categorys
        .slice(zero, cinco)
        .map(({ strCategory }, index) => (
          <button
            type="button"
            key={ index }
            data-testid={ `${strCategory}-category-filter` }
            hidden={ showSearch ? bools : false }
            value={ strCategory }
            onMouseMove={ () => setToggle(false) }
            onClick={ async ({ target }) => {
              setToggle(false);
              const term = toggle ? '' : target.value;
              const { meals } = await mealsAPI(term, 'c');
              setMealsData(meals);
              setToggle(true);
            } }
          >
            { strCategory}
          </button>
        ))}
    </div>
  );
}

export default Category;
